import React, { useState, useEffect } from 'react';

const CourseManagement = ({ courses, onCourseUpdate }) => {
  const [showForm, setShowForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [formData, setFormData] = useState({ name: '', instructor: '' });

  // Reset form when switching between add and edit modes
  useEffect(() => {
    if (editingCourse) {
      setFormData({
        name: editingCourse.name,
        instructor: editingCourse.instructor
      });
    } else {
      setFormData({ name: '', instructor: '' });
    }
  }, [editingCourse]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      if (editingCourse) {
        // Update existing course
        const response = await fetch(`http://localhost:3001/courses/${editingCourse.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            ...editingCourse,
            name: formData.name,
            instructor: formData.instructor
          }),
        });
        
        if (response.ok) {
          alert('Course updated successfully!');
          onCourseUpdate();
        } else {
          alert('Failed to update course');
        }
      } else {
        // Create new course
        const response = await fetch('http://localhost:3001/courses', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            name: formData.name,
            instructor: formData.instructor
          }),
        });
        
        if (response.ok) {
          alert('Course created successfully!');
          onCourseUpdate();
        } else {
          alert('Failed to create course');
        }
      }
      
      setFormData({ name: '', instructor: '' });
      setEditingCourse(null);
      setShowForm(false);
    } catch (error) {
      console.error('Error saving course:', error);
      alert('Error saving course. Please check the console for details.');
    }
  };

  const handleEdit = (course) => {
    setEditingCourse(course);
    setShowForm(true);
  };

  const handleDelete = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      try {
        // First check if there are any feedbacks for this course
        const feedbacksResponse = await fetch('http://localhost:3001/feedbacks');
        const feedbacks = await feedbacksResponse.json();
        const courseFeedbacks = feedbacks.filter(f => f.courseId === courseId);
        
        if (courseFeedbacks.length > 0) {
          if (!window.confirm(`This course has ${courseFeedbacks.length} feedback(s). Deleting it will also remove all associated feedbacks. Continue?`)) {
            return;
          }
          
          // Delete all feedbacks for this course
          for (const feedback of courseFeedbacks) {
            await fetch(`http://localhost:3001/feedbacks/${feedback.id}`, {
              method: 'DELETE'
            });
          }
        }
        
        // Delete the course
        const response = await fetch(`http://localhost:3001/courses/${courseId}`, {
          method: 'DELETE'
        });
        
        if (response.ok) {
          alert('Course deleted successfully!');
          onCourseUpdate();
        } else {
          alert('Failed to delete course');
        }
      } catch (error) {
        console.error('Error deleting course:', error);
        alert('Error deleting course. Please check the console for details.');
      }
    }
  };

  const cancelForm = () => {
    setShowForm(false);
    setEditingCourse(null);
    setFormData({ name: '', instructor: '' });
  };

  return (
    <div className="course-management">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="text-primary">üìö Course Management</h2>
        <button 
          className="btn btn-primary btn-lg"
          onClick={() => {
            setEditingCourse(null);
            setShowForm(!showForm);
          }}
          disabled={showForm && !editingCourse}
        >
          {showForm && !editingCourse ? '‚úñ Cancel' : '‚ûï Add New Course'}
        </button>
      </div>

      {(showForm || editingCourse) && (
        <div className="card shadow-lg mb-4">
          <div className="card-header bg-primary text-white">
            <h5 className="mb-0">{editingCourse ? 'Edit Course' : 'Add New Course'}</h5>
          </div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label">Course Name</label>
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    value={formData.name}
                    onChange={(e) => setFormData({...formData, name: e.target.value})}
                    placeholder="Enter course name"
                    required
                  />
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">Instructor</label>
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    value={formData.instructor}
                    onChange={(e) => setFormData({...formData, instructor: e.target.value})}
                    placeholder="Enter instructor name"
                    required
                  />
                </div>
              </div>
              <div className="d-flex gap-2">
                <button type="submit" className="btn btn-success btn-lg">
                  {editingCourse ? 'üíæ Update Course' : 'üíæ Create Course'}
                </button>
                <button 
                  type="button" 
                  className="btn btn-secondary btn-lg"
                  onClick={cancelForm}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="card shadow-lg">
        <div className="card-header bg-info text-white">
          <h5 className="mb-0">üìã Existing Courses ({courses.length})</h5>
        </div>
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-hover table-striped">
              <thead className="table-dark">
                <tr>
                  <th>ID</th>
                  <th>Course Name</th>
                  <th>Instructor</th>
                  <th>Rating</th>
                  <th>Feedbacks</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {courses.map((course) => (
                  <tr key={course.id}>
                    <td><strong>#{course.id}</strong></td>
                    <td>{course.name}</td>
                    <td>{course.instructor}</td>
                    <td>
                      <span className={`badge ${course.avgRating >= 4 ? 'bg-success' : course.avgRating >= 3 ? 'bg-warning' : 'bg-danger'}`}>
                        ‚≠ê {course.avgRating}/5
                      </span>
                    </td>
                    <td>
                      <span className="badge bg-primary">
                        üìù {course.feedbackCount}
                      </span>
                    </td>
                    <td>
                      <div className="btn-group btn-group-sm">
                        <button 
                          className="btn btn-outline-primary"
                          onClick={() => handleEdit(course)}
                        >
                          ‚úèÔ∏è Edit
                        </button>
                        <button 
                          className="btn btn-outline-danger"
                          onClick={() => handleDelete(course.id)}
                        >
                          üóëÔ∏è Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          
          {courses.length === 0 && (
            <div className="text-center py-4">
              <div className="text-muted">
                <h4>üì≠ No courses found</h4>
                <p>Click "Add New Course" to create your first course!</p>
              </div>
            </div>
          )}
        </div>
      </div>

      <div className="alert alert-info mt-4">
        <strong>üí° Note:</strong> Full CRUD operations are now implemented with backend API integration.
      </div>
    </div>
  );
};

export default CourseManagement;
