import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:5000/courses';

const CourseManagement = () => {
  const [courses, setCourses] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ id: null, name: '', instructor: '' });
  const [isEditing, setIsEditing] = useState(false);

  // Fetch courses from API
  const fetchCourses = async () => {
    const res = await fetch(API_URL);
    const data = await res.json();
    setCourses(data);
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isEditing) {
      // Update course
      await fetch(`${API_URL}/${formData.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: formData.name, instructor: formData.instructor }),
      });
    } else {
      // Add new course
      await fetch(API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: formData.name, instructor: formData.instructor }),
      });
    }

    setFormData({ id: null, name: '', instructor: '' });
    setShowForm(false);
    setIsEditing(false);
    fetchCourses();
  };

  const handleEdit = (course) => {
    setFormData(course);
    setShowForm(true);
    setIsEditing(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      await fetch(`${API_URL}/${id}`, { method: 'DELETE' });
      fetchCourses();
    }
  };

  return (
    <div className="course-management">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="text-primary">📚 Course Management</h2>
        <button className="btn btn-primary btn-lg" onClick={() => { setShowForm(!showForm); setIsEditing(false); setFormData({ id: null, name: '', instructor: '' }) }}>
          {showForm ? '✖ Cancel' : '➕ Add New Course'}
        </button>
      </div>

      {showForm && (
        <div className="card shadow-lg mb-4">
          <div className="card-header bg-primary text-white">
            <h5 className="mb-0">{isEditing ? 'Edit Course' : 'Add New Course'}</h5>
          </div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label">Course Name</label>
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    placeholder="Enter course name"
                    required
                  />
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">Instructor</label>
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    value={formData.instructor}
                    onChange={(e) => setFormData({ ...formData, instructor: e.target.value })}
                    placeholder="Enter instructor name"
                    required
                  />
                </div>
              </div>
              <div className="d-flex gap-2">
                <button type="submit" className="btn btn-success btn-lg">
                  💾 {isEditing ? 'Update Course' : 'Create Course'}
                </button>
                <button type="button" className="btn btn-secondary btn-lg" onClick={() => setShowForm(false)}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="card shadow-lg">
        <div className="card-header bg-info text-white">
          <h5 className="mb-0">📋 Existing Courses ({courses.length})</h5>
        </div>
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-hover table-striped">
              <thead className="table-dark">
                <tr>
                  <th>ID</th>
                  <th>Course Name</th>
                  <th>Instructor</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {courses.map((course) => (
                  <tr key={course.id}>
                    <td><strong>#{course.id}</strong></td>
                    <td>{course.name}</td>
                    <td>{course.instructor}</td>
                    <td>
                      <div className="btn-group btn-group-sm">
                        <button className="btn btn-outline-primary" onClick={() => handleEdit(course)}>✏️ Edit</button>
                        <button className="btn btn-outline-danger" onClick={() => handleDelete(course.id)}>🗑️ Delete</button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {courses.length === 0 && (
            <div className="text-center py-4">
              <div className="text-muted">
                <h4>📭 No courses found</h4>
                <p>Click "Add New Course" to create your first course!</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CourseManagement;
