import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

import Navbar from './components/Navbar';
import CourseList from './components/CourseList';
import FeedbackForm from './components/FeedbackForm';
import Analytics from './components/Analytics';

// API base URL - points to your backend
const API_BASE = 'http://localhost:5000/api';

function App() {
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch courses from backend API
  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE}/courses`);
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      setCourses(data);
      setError('');
    } catch (error) {
      console.error('Error fetching courses:', error);
      setError('Failed to load courses. Please check if the backend server is running.');
      
      // Fallback to mock data if backend is not available
      const mockCourses = [
        { id: 1, name: 'Mathematics', instructor: 'Dr. Smith', avgRating: 4.2 },
        { id: 2, name: 'Computer Science', instructor: 'Prof. Johnson', avgRating: 4.5 },
        { id: 3, name: 'Physics', instructor: 'Dr. Brown', avgRating: 3.8 },
      ];
      setCourses(mockCourses);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="App">
        <Navbar />
        <div className="container mt-4 text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Loading courses...</p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Navbar />
        <div className="container mt-4">
          {error && (
            <div className="alert alert-warning alert-dismissible fade show" role="alert">
              <strong>Warning:</strong> {error}
              <button type="button" className="btn-close" onClick={() => setError('')}></button>
            </div>
          )}
          
          <Routes>
            <Route 
              path="/" 
              element={
                <CourseList 
                  courses={courses} 
                  setSelectedCourse={setSelectedCourse} 
                />
              } 
            />
            <Route 
              path="/feedback" 
              element={
                <FeedbackForm 
                  course={selectedCourse} 
                  courses={courses} 
                  onFeedbackSubmit={fetchCourses} // Refresh courses after feedback
                />
              } 
            />
            <Route 
              path="/analytics" 
              element={
                <Analytics 
                  courses={courses} 
                />
              } 
            />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
