import React, { useState, useEffect } from 'react';

const CourseManagement = ({ courses, onCourseUpdate }) => {
  const [showForm, setShowForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [formData, setFormData] = useState({ name: '', instructor: '' });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  // Reset form when switching between add and edit modes
  useEffect(() => {
    if (editingCourse) {
      setFormData({
        name: editingCourse.name || '',
        instructor: editingCourse.instructor || ''
      });
    } else {
      setFormData({ name: '', instructor: '' });
    }
  }, [editingCourse]);

  // Ensure courses is always an array
  const safeCourses = Array.isArray(courses) ? courses : [];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');
    
    try {
      const apiUrl = 'http://localhost:3001'; // Change this if your server runs on a different port
      
      if (editingCourse) {
        // Update existing course
        const response = await fetch(`${apiUrl}/courses/${editingCourse.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            ...editingCourse,
            name: formData.name,
            instructor: formData.instructor
          }),
        });
        
        if (response.ok) {
          alert('Course updated successfully!');
          onCourseUpdate();
        } else {
          throw new Error('Failed to update course');
        }
      } else {
        // Create new course - generate a simple ID if needed
        const newCourse = {
          name: formData.name,
          instructor: formData.instructor,
          // Generate a simple ID if the backend doesn't provide one
          id: Math.max(...safeCourses.map(c => c.id || 0), 0) + 1
        };
        
        const response = await fetch(`${apiUrl}/courses`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(newCourse),
        });
        
        if (response.ok) {
          alert('Course created successfully!');
          onCourseUpdate();
        } else {
          // If the API call fails, try to add locally
          console.warn('API call failed, adding course locally');
          if (typeof onCourseUpdate === 'function') {
            // This assumes the parent component can handle local state updates
            onCourseUpdate([...safeCourses, newCourse]);
          }
        }
      }
      
      setFormData({ name: '', instructor: '' });
      setEditingCourse(null);
      setShowForm(false);
    } catch (error) {
      console.error('Error saving course:', error);
      setError('Error saving course. Please make sure the JSON server is running on port 3001.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleEdit = (course) => {
    setEditingCourse(course);
    setShowForm(true);
  };

  const handleDelete = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      setIsLoading(true);
      try {
        const apiUrl = 'http://localhost:3001';
        
        // First check if there are any feedbacks for this course
        try {
          const feedbacksResponse = await fetch(`${apiUrl}/feedbacks`);
          if (feedbacksResponse.ok) {
            const feedbacks = await feedbacksResponse.json();
            const courseFeedbacks = feedbacks.filter(f => f.courseId === courseId);
            
            if (courseFeedbacks.length > 0) {
              if (!window.confirm(`This course has ${courseFeedbacks.length} feedback(s). Deleting it will also remove all associated feedbacks. Continue?`)) {
                return;
              }
              
              // Delete all feedbacks for this course
              for (const feedback of courseFeedbacks) {
                await fetch(`${apiUrl}/feedbacks/${feedback.id}`, {
                  method: 'DELETE'
                });
              }
            }
          }
        } catch (e) {
          console.warn('Could not check feedbacks:', e);
        }
        
        // Delete the course
        const response = await fetch(`${apiUrl}/courses/${courseId}`, {
          method: 'DELETE'
        });
        
        if (response.ok) {
          alert('Course deleted successfully!');
          onCourseUpdate();
        } else {
          // If API fails, try to delete locally
          console.warn('API delete failed, removing course locally');
          if (typeof onCourseUpdate === 'function') {
            onCourseUpdate(safeCourses.filter(course => course.id !== courseId));
          }
        }
      } catch (error) {
        console.error('Error deleting course:', error);
        setError('Error deleting course. Please make sure the JSON server is running.');
        
        // Fallback: delete locally if API fails
        if (typeof onCourseUpdate === 'function') {
          onCourseUpdate(safeCourses.filter(course => course.id !== courseId));
        }
      } finally {
        setIsLoading(false);
      }
    }
  };

  const cancelForm = () => {
    setShowForm(false);
    setEditingCourse(null);
    setFormData({ name: '', instructor: '' });
    setError('');
  };

  return (
    <div className="course-management">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="text-primary">📚 Course Management</h2>
        <button 
          className="btn btn-primary btn-lg"
          onClick={() => {
            setEditingCourse(null);
            setShowForm(!showForm);
          }}
          disabled={(showForm && !editingCourse) || isLoading}
        >
          {showForm && !editingCourse ? '✖ Cancel' : '➕ Add New Course'}
        </button>
      </div>

      {error && (
        <div className="alert alert-danger">
          <strong>Error:</strong> {error}
          <div className="mt-2">
            <small>
              Make sure your JSON server is running with: <code>npx json-server --watch db.json --port 3001</code>
            </small>
          </div>
        </div>
      )}

      {(showForm || editingCourse) && (
        <div className="card shadow-lg mb-4">
          <div className="card-header bg-primary text-white">
            <h5 className="mb-0">{editingCourse ? 'Edit Course' : 'Add New Course'}</h5>
          </div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label">Course Name</label>
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    value={formData.name}
                    onChange={(e) => setFormData({...formData, name: e.target.value})}
                    placeholder="Enter course name"
                    required
                    disabled={isLoading}
                  />
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">Instructor</label>
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    value={formData.instructor}
                    onChange={(e) => setFormData({...formData, instructor: e.target.value})}
                    placeholder="Enter instructor name"
                    required
                    disabled={isLoading}
                  />
                </div>
              </div>
              <div className="d-flex gap-2">
                <button 
                  type="submit" 
                  className="btn btn-success btn-lg"
                  disabled={isLoading}
                >
                  {isLoading ? '⏳ Processing...' : (editingCourse ? '💾 Update Course' : '💾 Create Course')}
                </button>
                <button 
                  type="button" 
                  className="btn btn-secondary btn-lg"
                  onClick={cancelForm}
                  disabled={isLoading}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="card shadow-lg">
        <div className="card-header bg-info text-white">
          <h5 className="mb-0">📋 Existing Courses ({safeCourses.length})</h5>
        </div>
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-hover table-striped">
              <thead className="table-dark">
                <tr>
                  <th>ID</th>
                  <th>Course Name</th>
                  <th>Instructor</th>
                  <th>Rating</th>
                  <th>Feedbacks</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {safeCourses.map((course) => (
                  <tr key={course.id}>
                    <td><strong>#{course.id}</strong></td>
                    <td>{course.name}</td>
                    <td>{course.instructor}</td>
                    <td>
                      <span className={`badge ${course.avgRating >= 4 ? 'bg-success' : course.avgRating >= 3 ? 'bg-warning' : 'bg-danger'}`}>
                        ⭐ {course.avgRating || 'N/A'}/5
                      </span>
                    </td>
                    <td>
                      <span className="badge bg-primary">
                        📝 {course.feedbackCount || 0}
                      </span>
                    </td>
                    <td>
                      <div className="btn-group btn-group-sm">
                        <button 
                          className="btn btn-outline-primary"
                          onClick={() => handleEdit(course)}
                          disabled={isLoading}
                        >
                          ✏️ Edit
                        </button>
                        <button 
                          className="btn btn-outline-danger"
                          onClick={() => handleDelete(course.id)}
                          disabled={isLoading}
                        >
                          🗑️ Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          
          {safeCourses.length === 0 && (
            <div className="text-center py-4">
              <div className="text-muted">
                <h4>📭 No courses found</h4>
                <p>Click "Add New Course" to create your first course!</p>
              </div>
            </div>
          )}
        </div>
      </div>

      <div className="alert alert-info mt-4">
        <strong>💡 Note:</strong> Full CRUD operations are now implemented with backend API integration.
        {!safeCourses.length && " Using mock data until server connection is established."}
      </div>
    </div>
  );
};

export default CourseManagement;
