import React, { useState, useEffect } from 'react';

const API_BASE = 'http://localhost:5000/api';

function App() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await fetch(`${API_BASE}/courses`);
      const data = await response.json();
      setCourses(data);
    } catch (error) {
      console.error('Error:', error);
      // Fallback data
      setCourses([
        { id: 1, name: 'Mathematics', instructor: 'Dr. Smith', avgRating: 4.2 },
        { id: 2, name: 'Computer Science', instructor: 'Prof. Johnson', avgRating: 4.5 },
        { id: 3, name: 'Physics', instructor: 'Dr. Brown', avgRating: 3.8 },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const submitTestFeedback = async () => {
    try {
      const response = await fetch(`${API_BASE}/feedback`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          courseId: 1,
          rating: 5,
          comment: 'Test feedback from button'
        }),
      });
      const result = await response.json();
      alert('Feedback submitted: ' + JSON.stringify(result));
      fetchCourses(); // Refresh courses
    } catch (error) {
      console.error('Error submitting feedback:', error);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <h1>Student Feedback System</h1>
      
      <button onClick={submitTestFeedback} style={{ 
        padding: '10px', 
        margin: '10px', 
        backgroundColor: '#007bff', 
        color: 'white', 
        border: 'none',
        borderRadius: '5px'
      }}>
        Submit Test Feedback for Mathematics
      </button>

      <h2>Courses:</h2>
      <div>
        {courses.map(course => (
          <div key={course.id} style={{ 
            border: '1px solid #ccc', 
            padding: '10px', 
            margin: '10px',
            borderRadius: '5px'
          }}>
            <h3>{course.name}</h3>
            <p>Instructor: {course.instructor}</p>
            <p>Rating: {course.avgRating}/5</p>
            <a href={`http://localhost:5000/api/analytics/${course.id}`} target="_blank" rel="noopener noreferrer">
              View Analytics (API)
            </a>
          </div>
        ))}
      </div>

      <div style={{ marginTop: '20px' }}>
        <h3>Test API Endpoints:</h3>
        <ul>
          <li><a href="http://localhost:5000/api/health" target="_blank" rel="noopener noreferrer">Health Check</a></li>
          <li><a href="http://localhost:5000/api/courses" target="_blank" rel="noopener noreferrer">All Courses</a></li>
          <li><a href="http://localhost:5000/api/analytics/1" target="_blank" rel="noopener noreferrer">Analytics for Course 1</a></li>
        </ul>
      </div>
    </div>
  );
}

export default App;
