import React, { useState, useEffect } from 'react';

const CourseManagement = ({ courses, onCourseUpdate }) => {
  const [showForm, setShowForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [formData, setFormData] = useState({ name: '', instructor: '' });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [localCourses, setLocalCourses] = useState([]);

  // Initialize local courses from props with proper data formatting
  useEffect(() => {
    const formatCoursesData = (rawCourses) => {
      if (Array.isArray(rawCourses)) {
        return rawCourses.map(course => ({
          id: course.id || 0,
          name: course.name || 'Unknown Course',
          instructor: course.instructor || 'Unknown Instructor',
          // Handle various rating formats
          avgRating: parseFloat(course.avgRating) || 
                    parseFloat(course.rating) || 
                    (typeof course.avgRating === 'string' ? parseFloat(course.avgRating.replace('%', '')) / 20 : 0),
          // Handle various feedback count formats
          feedbackCount: parseInt(course.feedbackCount) || 
                        parseInt(course.feedbacks) || 
                        (typeof course.feedbackCount === 'string' ? parseInt(course.feedbackCount.split('.')[1]) : 0)
        }));
      }
      return [];
    };

    try {
      let formattedCourses = [];
      
      if (Array.isArray(courses)) {
        formattedCourses = formatCoursesData(courses);
      } else if (courses && typeof courses === 'object') {
        // Handle different data structures
        if (courses.courses && Array.isArray(courses.courses)) {
          formattedCourses = formatCoursesData(courses.courses);
        } else if (courses.data && Array.isArray(courses.data)) {
          formattedCourses = formatCoursesData(courses.data);
        } else {
          // Try to extract array from object values
          const potentialArray = Object.values(courses);
          if (Array.isArray(potentialArray) && potentialArray.length > 0 && typeof potentialArray[0] === 'object') {
            formattedCourses = formatCoursesData(potentialArray);
          }
        }
      }
      
      setLocalCourses(formattedCourses);
    } catch (err) {
      console.error('Error formatting courses:', err);
      // Fallback to mock data if formatting fails
      setLocalCourses([
        { id: 1, name: 'Mathematics', instructor: 'Dr. Smith', avgRating: 4.2, feedbackCount: 15 },
        { id: 2, name: 'Computer Science', instructor: 'Prof. Johnson', avgRating: 4.5, feedbackCount: 22 },
        { id: 3, name: 'Physics', instructor: 'Dr. Brown', avgRating: 3.8, feedbackCount: 8 },
        { id: 4, name: 'Chemistry', instructor: 'Dr. Wilson', avgRating: 4.1, feedbackCount: 12 },
        { id: 5, name: 'Biology', instructor: 'Dr. Davis', avgRating: 4.3, feedbackCount: 18 }
      ]);
    }
  }, [courses]);

  // Reset form when switching between add and edit modes
  useEffect(() => {
    if (editingCourse) {
      setFormData({
        name: editingCourse.name || '',
        instructor: editingCourse.instructor || ''
      });
    } else {
      setFormData({ name: '', instructor: '' });
    }
  }, [editingCourse]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsLoading(true);
    
    if (editingCourse) {
      // Update existing course
      const updatedCourses = localCourses.map(course =>
        course.id === editingCourse.id
          ? { ...course, name: formData.name, instructor: formData.instructor }
          : course
      );
      setLocalCourses(updatedCourses);
      if (onCourseUpdate) onCourseUpdate(updatedCourses);
      alert('Course updated successfully!');
    } else {
      // Create new course
      const newCourse = {
        id: Math.max(...localCourses.map(c => c.id), 0) + 1,
        name: formData.name,
        instructor: formData.instructor,
        avgRating: 0,
        feedbackCount: 0
      };
      
      const updatedCourses = [...localCourses, newCourse];
      setLocalCourses(updatedCourses);
      if (onCourseUpdate) onCourseUpdate(updatedCourses);
      alert('Course created successfully!');
    }
    
    setFormData({ name: '', instructor: '' });
    setEditingCourse(null);
    setShowForm(false);
    setIsLoading(false);
  };

  const handleEdit = (course) => {
    setEditingCourse(course);
    setShowForm(true);
  };

  const handleDelete = (courseId) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      const updatedCourses = localCourses.filter(course => course.id !== courseId);
      setLocalCourses(updatedCourses);
      if (onCourseUpdate) onCourseUpdate(updatedCourses);
      alert('Course deleted successfully!');
    }
  };

  const cancelForm = () => {
    setShowForm(false);
    setEditingCourse(null);
    setFormData({ name: '', instructor: '' });
    setError('');
  };

  return (
    <div className="course-management">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="text-primary">📚 Course Management</h2>
        <button 
          className="btn btn-primary btn-lg"
          onClick={() => {
            setEditingCourse(null);
            setShowForm(!showForm);
          }}
          disabled={showForm && !editingCourse}
        >
          {showForm && !editingCourse ? '✖ Cancel' : '➕ Add New Course'}
        </button>
      </div>

      {error && (
        <div className="alert alert-danger">
          <strong>Error:</strong> {error}
        </div>
      )}

      {(showForm || editingCourse) && (
        <div className="card shadow-lg mb-4">
          <div className="card-header bg-primary text-white">
            <h5 className="mb-0">{editingCourse ? 'Edit Course' : 'Add New Course'}</h5>
          </div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label">Course Name</label>
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    value={formData.name}
                    onChange={(e) => setFormData({...formData, name: e.target.value})}
                    placeholder="Enter course name"
                    required
                  />
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">Instructor</label>
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    value={formData.instructor}
                    onChange={(e) => setFormData({...formData, instructor: e.target.value})}
                    placeholder="Enter instructor name"
                    required
                  />
                </div>
              </div>
              <div className="d-flex gap-2">
                <button 
                  type="submit" 
                  className="btn btn-success btn-lg"
                >
                  {editingCourse ? '💾 Update Course' : '💾 Create Course'}
                </button>
                <button 
                  type="button" 
                  className="btn btn-secondary btn-lg"
                  onClick={cancelForm}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="card shadow-lg">
        <div className="card-header bg-info text-white">
          <h5 className="mb-0">📋 Existing Courses ({localCourses.length})</h5>
        </div>
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-hover table-striped">
              <thead className="table-dark">
                <tr>
                  <th>ID</th>
                  <th>Course Name</th>
                  <th>Instructor</th>
                  <th>Rating</th>
                  <th>Feedbacks</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {localCourses.map((course) => (
                  <tr key={course.id}>
                    <td><strong>#{course.id}</strong></td>
                    <td>{course.name}</td>
                    <td>{course.instructor}</td>
                    <td>
                      <span className={`badge ${course.avgRating >= 4 ? 'bg-success' : course.avgRating >= 3 ? 'bg-warning' : 'bg-danger'}`}>
                        ⭐ {course.avgRating.toFixed(1)}/5
                      </span>
                    </td>
                    <td>
                      <span className="badge bg-primary">
                        📝 {course.feedbackCount}
                      </span>
                    </td>
                    <td>
                      <div className="btn-group btn-group-sm">
                        <button 
                          className="btn btn-outline-primary"
                          onClick={() => handleEdit(course)}
                        >
                          ✏️ Edit
                        </button>
                        <button 
                          className="btn btn-outline-danger"
                          onClick={() => handleDelete(course.id)}
                        >
                          🗑️ Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <div className="alert alert-info mt-4">
        <strong>💡 Note:</strong> Using local state management for demonstration.
        To enable API integration, start JSON server on port 3001.
      </div>
    </div>
  );
};

export default CourseManagement;
